// Parse qualified leads and filter for unsent emails
const leads = JSON.parse($input.item.binary.data.data.toString('utf8'));

// Filter leads that haven't been emailed yet
const unsentLeads = leads.filter(lead => 
  lead.status !== 'emailed' && 
  (lead.email || lead.potentialEmails?.length > 0)
);

return unsentLeads.map(lead => ({
  json: {
    id: lead.name + '-' + lead.address,
    name: lead.name,
    email: lead.email || lead.potentialEmails?.[0],
    address: lead.address,
    phone: lead.phone,
    website: lead.website,
    qualificationScore: lead.qualificationScore,
    issues: lead.issues || [],
    city: lead.city,
    state: lead.state,
    status: lead.status || 'new'
  }
}));

// Prepare personalized email based on restaurant issues
const lead = $input.item.json;
const issues = lead.issues || [];

let personalizedHook = '';
if (issues.includes('not_on_doordash')) {
  personalizedHook = 'We noticed you\'re not on DoorDash yetâ€”this is a huge opportunity to unlock 20-40% more revenue from delivery customers.';
} else if (issues.includes('no_website') || issues.includes('broken_website')) {
  personalizedHook = 'We noticed your restaurant could benefit from a stronger online presenceâ€”especially a shareable digital menu that customers can access anywhere.';
} else if (issues.includes('no_menu_photos') || issues.includes('no_professional_photos')) {
  personalizedHook = 'Your menu items deserve to look as amazing as they taste. We can transform your current photos into stunning visuals that drive orders.';
} else {
  personalizedHook = 'We help restaurants like yours transform their menu presentation and digital presence to drive more sales.';
}

