const text = `Hi there!\n\nMy name is Sydney, and I'm reaching out from MagicPlate.ai. ${personalizedHook}\n\nWe specialize in AI-powered menu restoration and digital menu creation. Starting at $299.\n\nBook your call: https://magicplate.info/book\nReply to this email or call (805) 668-9973\n\nBest,\nSydney Ramey\nMagicPlate.ai`;

return [{
  json: {
    ...lead,
    emailHtml: html,
    emailText: text,
    personalizedHook: personalizedHook
  }
}];

// Update lead status in qualified-leads.json
const lead = $input.item.json;
const fs = require('fs');
const path = require('path');

const leadsPath = path.join(process.env.WORKSPACE_PATH || '.', 'data', 'qualified-leads.json');

// Read current leads
let leads = [];
try {
  const data = fs.readFileSync(leadsPath, 'utf8');
  leads = JSON.parse(data);
} catch (error) {
  console.log('Could not read leads file');
}

// Update the lead
const leadIndex = leads.findIndex(l => 
  (l.name + '-' + l.address).toLowerCase() === lead.id.toLowerCase()
);

if (leadIndex !== -1) {
  leads[leadIndex].status = 'emailed';
  leads[leadIndex].emailedAt = new Date().toISOString();
  leads[leadIndex].emailUsed = lead.email;

  // Write back
  fs.writeFileSync(leadsPath, JSON.stringify(leads, null, 2));
}

// Also update sent-emails.json
const trackingPath = path.join(process.env.WORKSPACE_PATH || '.', 'data', 'sent-emails.json');
let tracking = { sent: [], stats: { total: 0, successful: 0, failed: 0 } };

try {
  const data = fs.readFileSync(trackingPath, 'utf8');
  tracking = JSON.parse(data);
} catch (error) {
  // File doesn't exist, create new
}

tracking.sent.push({
  restaurant: lead.name,
  email: lead.email,
  success: true,
  score: lead.qualificationScore,
  issues: lead.issues,
  sentAt: new Date().toISOString()
});

tracking.stats.total++;
tracking.stats.successful++;

fs.writeFileSync(trackingPath, JSON.stringify(tracking, null, 2));

return [{ json: { ...lead, updated: true } }];

// Process email reply from Resend webhook
const webhookData = $input.item.json;

// Extract reply information
const replyData = {
  from: webhookData.body?.from || webhookData.from,
  to: webhookData.body?.to || webhookData.to,
  subject: webhookData.body?.subject || webhookData.subject,
  text: webhookData.body?.text || webhookData.text,
  receivedAt: new Date().toISOString()
};

// Update sent-emails.json to mark as replied
const fs = require('fs');
const path = require('path');
const trackingPath = path.join(process.env.WORKSPACE_PATH || '.', 'data', 'sent-emails.json');

let tracking = { sent: [], stats: { total: 0, successful: 0, failed: 0 } };

try {
  const data = fs.readFileSync(trackingPath, 'utf8');
  tracking = JSON.parse(data);
} catch (error) {
  // File doesn't exist
}

// Find and update the email
const emailIndex = tracking.sent.findIndex(e => e.email === replyData.from);
if (emailIndex !== -1) {
  tracking.sent[emailIndex].replied = true;
  tracking.sent[emailIndex].replyReceivedAt = replyData.receivedAt;
  tracking.sent[emailIndex].replyText = replyData.text;

  fs.writeFileSync(trackingPath, JSON.stringify(tracking, null, 2));
}

return [{ json: replyData }];
